;
; zapper_ready = pad1_zapper ^ 1; // XOR last frame, make sure not held down still
;
	lda     _pad1_zapper
	eor     #$01
	sta     _zapper_ready
;
; pad1_zapper = zap_shoot(1);   // controller slot 2
;
	lda     #$01
	jsr     _zap_shoot
	sta     _pad1_zapper
;
; trigger_pulled = (pad1 & PAD_B) || (pad1 & PAD_A) || zap_shoot(1);                // controller slot 1 zapper
